(()=>{"use strict";(t=>{const e=document.querySelector("#timer-days"),n=document.querySelector("#timer-hours"),o=document.querySelector("#timer-minutes"),r=document.querySelector("#timer-seconds"),s=()=>{let t=(t=>{let e=(new Date("1 january 2022").getTime()-(new Date).getTime())/1e3;return{timeRemaining:e,days:Math.floor(e/3600/24),hours:Math.floor(e/3600%24),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();if(t.timeRemaining>0){for(let e in t)t[e]<10&&(t[e]="0"+t[e]);e.textContent=t.days,n.textContent=t.hours,o.textContent=t.minutes,r.textContent=t.seconds,setInterval(s,1e3)}else e.textContent="00",n.textContent="00",o.textContent="00",r.textContent="00"};s()})()})();